query:
UPDATE sucesion_puestos sp
SET sp.cargoSucesor = (
  SELECT e.cargo FROM empleados e WHERE e.nombre = sp.sucesor LIMIT 1
),
sp.departamentoSucesor = (
  SELECT e.departamento FROM empleados e WHERE e.nombre = sp.sucesor LIMIT 1
)
WHERE sp.sucesor != '' AND sp.sucesor IS NOT NULL;

