query:
-- Verificar planes recientes
SELECT id, colaborador, cargo, tipoReemplazo, reemplazo FROM planes_sustitucion ORDER BY createdAt DESC LIMIT 5;

-- Verificar reemplazos en tabla plan_reemplazos
SELECT * FROM plan_reemplazos ORDER BY createdAt DESC LIMIT 10;

-- Contar reemplazos por plan
SELECT planSustitucionId, COUNT(*) as cantidad_reemplazos FROM plan_reemplazos GROUP BY planSustitucionId;

