-- Deshabilitar verificación de claves foráneas temporalmente
SET FOREIGN_KEY_CHECKS = 0;

-- Limpiar todas las tablas en orden de dependencias
TRUNCATE TABLE auditoriaPlanesAccion;
TRUNCATE TABLE comentariosPlanesAccion;
TRUNCATE TABLE planesAccion;
TRUNCATE TABLE planesSuccesion;
TRUNCATE TABLE planesSustitucion;
TRUNCATE TABLE empleados;
TRUNCATE TABLE usuarios;

-- Reabilitar verificación de claves foráneas
SET FOREIGN_KEY_CHECKS = 1;

-- Verificar que las tablas están vacías
SELECT 
  'empleados' as tabla, COUNT(*) as registros FROM empleados
UNION ALL
SELECT 'usuarios', COUNT(*) FROM usuarios
UNION ALL
SELECT 'planesSustitucion', COUNT(*) FROM planesSustitucion
UNION ALL
SELECT 'planesSuccesion', COUNT(*) FROM planesSuccesion
UNION ALL
SELECT 'planesAccion', COUNT(*) FROM planesAccion
UNION ALL
SELECT 'comentariosPlanesAccion', COUNT(*) FROM comentariosPlanesAccion
UNION ALL
SELECT 'auditoriaPlanesAccion', COUNT(*) FROM auditoriaPlanesAccion;

